document.body.onload=initializeTheme,setTimeout(()=>pd2.focus(),200);const ht=document.querySelector("html"),sb=document.querySelector(".sb"),sv=document.querySelector(".sbs"),hb=document.querySelector("#sb-btn"),ema=document.querySelector("#ema"),cs=document.querySelector("#cs"),cp=document.querySelector(".copy");function initializeTheme(){"on"===localStorage.getItem("dark")?(ht.setAttribute("data-theme","dark"),ht.className="bd"):(ht.removeAttribute("data-theme"),ht.className="bl")}function toggleDropdown(e,t,a){"true"===e.getAttribute("aria-expanded")?(t.classList.remove(a),e.setAttribute("aria-expanded","false")):(t.classList.add(a),e.setAttribute("aria-expanded","true"))}hb.onclick=()=>{toggleDropdown(hb,sb,"sbb")},cp.onclick=()=>{ema.select(),ema.setSelectionRange(0,99999),navigator.clipboard.writeText(ema.value),alert("Copied E-mail address: "+ema.value)},document.addEventListener("keydown",e=>{"Escape"===e.key&&sb.classList.contains("sbb")&&toggleDropdown(hb,sb,"sbb")}),document.addEventListener("click",e=>{!sb.classList.contains("sbb")||sb.contains(e.target)||hb.contains(e.target)||toggleDropdown(hb,sb,"sbb")});